contracts/CertificateRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract CertificateRegistry is AccessControl {
    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");

    struct Certificate {
        string studentName;
        string ipfsCID;
        address issuer;
        uint256 timestamp;
        bool revoked;
        bool exists;
    }

    mapping(bytes32 => Certificate) public certificates;

    event CertificateIssued(bytes32 indexed certificateHash, string studentName, address indexed issuer, uint256 timestamp);
    event CertificateRevoked(bytes32 indexed certificateHash, address indexed admin);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function issueCertificate(bytes32 _hash, string memory _studentName, string memory _ipfsCID) external onlyRole(ISSUER_ROLE) {
        require(!certificates[_hash].exists, "Certificate already exists");
        
        certificates[_hash] = Certificate({
            studentName: _studentName,
            ipfsCID: _ipfsCID,
            issuer: msg.sender,
            timestamp: block.timestamp,
            revoked: false,
            exists: true
        });

        emit CertificateIssued(_hash, _studentName, msg.sender, block.timestamp);
    }

    function verifyCertificate(bytes32 _hash) external view returns (bool, string memory, string memory, address, uint256, bool) {
        require(certificates[_hash].exists, "Certificate not found");
        Certificate memory cert = certificates[_hash];
        return (true, cert.studentName, cert.ipfsCID, cert.issuer, cert.timestamp, cert.revoked);
    }

    function revokeCertificate(bytes32 _hash) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(certificates[_hash].exists, "Certificate not found");
        require(!certificates[_hash].revoked, "Certificate already revoked");
        
        certificates[_hash].revoked = true;
        emit CertificateRevoked(_hash, msg.sender);
    }

    function grantIssuerRole(address _account) external onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(ISSUER_ROLE, _account);
    }
}

